mapper.select:
    select
    
    from ctn_menu a
    
			a.del_flag = #{delFlag}
			
				AND a.remark LIKE 
					
					
					CONCAT('%', #{remark}, '%')
			
			
				AND a.create_date between #{datemin} and #{datemax}

mapper.update:
UPDATE ctn_menu SET 
	del_flag = 1
WHERE id = #{id}

UPDATE ctn_menu SET 
	status = #{status}
WHERE id = #{id}
