mapper.select:
    select
    
    from pch_purchase_message a
     
			a.del_flag = #{delFlag}
			
				AND a.name LIKE 
					
					
					CONCAT('%', #{name}, '%')
			
			
				AND a.create_date between #{datemin} and #{datemax}

mapper.update:
UPDATE pch_purchase_message SET del_flag = 1
where id in
#{id}

UPDATE pch_purchase_message SET 
	del_flag = 1
WHERE id = #{id}
