mapper.select:
    select
    
    from pty_property a
    
			a.del_flag = #{delFlag}
			
				AND a.name LIKE 
					
					
					CONCAT('%', #{name}, '%')
			
			
				AND a.create_date between #{datemin} and #{datemax}

mapper.update:
UPDATE pty_property SET del_flag = 1
where id in
#{id}

UPDATE pty_property SET 
	del_flag = 1
WHERE id = #{id}

UPDATE pty_property SET 
	status = #{status}

WHERE id = #{id}

update pty_property
set update_by = #{updateBy.id,jdbcType=VARCHAR},
  update_date = #{updateDate,jdbcType=DATE},

  name = #{name,jdbcType=VARCHAR},
  type_id = #{ptyPropertyType.id,jdbcType=VARCHAR}
where id = #{id,jdbcType=VARCHAR}
