mapper.select:
    select
    
    from wh_warehouse a
     
			a.del_flag = #{delFlag}
			
				AND a.name LIKE 
					
					
					CONCAT('%', #{name}, '%')
			
				AND a.status =  #{status}
			
			
				AND a.create_date between #{datemin} and #{datemax}

mapper.update:
UPDATE wh_warehouse SET 
	del_flag = 1 
WHERE id = #{id}

UPDATE wh_warehouse SET del_flag = 1
where id in
#{id}

UPDATE wh_warehouse SET 
	status = #{status}
WHERE id = #{id}

UPDATE wh_warehouse SET 
	current_inventory = #{currentInventory}
WHERE id = #{id}

UPDATE wh_warehouse SET 
	whWarehouse_and_apply_code = #{whWarehouseAndApplyCode.id}
WHERE id = #{id}
